import R from 'ramda'

import motos from './mockMotos'
import categories from './mockCategories'

export const fetchMotos = async () => {
  return new Promise(resolve => {
    resolve(motos)
  })
}

export const loadMoreMotos = async ({offset}) => {
  return new Promise(resolve => {
    resolve(motos)
  })
}

export const fetchMotoById = async (id) => {
  return new Promise((resolve, reject) => {
    const moto = R.find(R.propEq('id', id), motos)
    resolve(moto)
  })
}

export const fetchCategories = async () => {
  return new Promise((resolve, reject) => {
    resolve(categories)
  })
}
