import React from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import {Link} from 'react-router'
import Header from 'components/header'
import Footer from 'components/footer'

import './media.css'
import './style.css'

import {
  getTotalBasketPrice,
  getBasketMotosWithCount,
} from 'selectors'

import {
  removeMotoFromBasket,
  basketCheckout,
  cleanBasket
} from 'actions'

const Basket = ({
  motos,
  totalPrice,
  basketCheckout,
  removeMotoFromBasket,
  cleanBasket
}) => {
  const isBasketEmpty = R.isEmpty(motos)

  const renderContent = () => {
    return (
      <div className='row '>
        {isBasketEmpty && <div className='textStyle'>Ваша корзина пуста :(</div>}

        <div className=''>
          <table className=' table '>
            <tbody>
            {motos.map((moto, index) => (
              <tr
                key={index}
                className='item-checout'
              >
                <td className='first-column-checkout'>
                  <img 
                    className='img-thumbnail imgbik'
                    src={moto.image}
                    alt={moto.name}
                  />
                </td>
                <td>{moto.name}</td>
                <td>${moto.price}</td>
                <td>{moto.count}</td>
                <td>
                    <span
                      className='delete-cart'
                      onClick={() => removeMotoFromBasket(moto.id)}
                    />
                </td>
              </tr>
            ))}
            </tbody>
          </table>
        </div>
        {
          R.not(isBasketEmpty) &&
          <div className='row'>
            <div className='textstl pull-right total-user-checkout'>
              <b >Итого:</b>
              ${totalPrice}
            </div>
          </div>
        }
      </div>
    )
  }

  const renderSidebar = () => (
    <div className="sidebarBasket">
      <Link
        className='btn btn-info botn'
        to='/catalog'
      >
        <span className='glyphicon glyphicon-info-sign'/>
        <span>Продолжить покупку</span>
      </Link>
      {
        R.not(isBasketEmpty) &&
        <div>
          <button
            onClick={cleanBasket}
            className='btn btn-danger botn'
          >
            <span className='glyphicon glyphicon-trash' />
            Очистить корзину
          </button>
          <button
            className='btn btn-success botn'
            onClick={() => basketCheckout(motos)}
          >
            <span className='glyphicon glyphicon-envelope' />
            Оплатить
          </button>
        </div>
      }
    </div>
  )


  return (
    
    <div className=''>
    <Header/>
      <div className='container'>
        <div className='row basketrow'>
          <div className='col-md-9 col-xs-12'>
            {renderContent()}
          </div>
          <div className='col-md-3 btn-user-checkout'>
            {renderSidebar()}
          </div>
        </div>
      </div>
      <Footer/>
    </div>
  )

}

const mapStateToProps = state => {
  return {
    motos: getBasketMotosWithCount(state),
    totalPrice: getTotalBasketPrice(state)
  }
}

const mapDispatchToProps = {
  basketCheckout,
  removeMotoFromBasket,
  cleanBasket

}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)

