import React, {Component} from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import {Link} from 'react-router'
import './style.css'


import {
  fetchMotos,
  loadMoreMotos,
  addMotoToBasket,
  fetchCategories
} from 'actions'
import {getMotos} from 'selectors'

class Motos extends Component {
  componentDidMount () {
    this.props.fetchMotos()
    this.props.fetchCategories()
  }

  renderMoto (moto, index) {
    const {addMotoToBasket} = this.props
    const shortDescription = `${R.take(60, moto.description)}...`

    return (
      <div className=' col-sm-4 col-lg-4 col-md-4 book-list ' key={index}>
        <div className='thumbnail cart'>
          <img
            className='img-thumbnail'
            src={moto.image}
            alt={moto.name}
          />
          <div className='caption'>
            <h4 className='pull-right'>${moto.price}</h4>
            <h4>
              <Link to={`/motos/${moto.id}`}>
                {moto.name}
              </Link>
            </h4>
            <p className='info'>{shortDescription}</p>
            <p className='itemButton'>
              <button
                className='btn button'
                onClick={() => addMotoToBasket(moto.id)}
              >
                Купить
              </button>
              <Link
                to={`/motos/${moto.id}`}
                className='btn btn-default'
              >
                Описание
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }


  render () {
    const {motos, loadMoreMotos} = this.props
    return (
      <div className='mainlayout'>
        <div className='books row '>
          {motos.map((moto, index) => this.renderMoto(moto, index))}
        </div>
        <div className='row'>
          <div className='col-md-12 loade_more'>
            <button
              onClick={loadMoreMotos}
              className=' btn button'
            >
              Показать еще...
            </button>
          </div>
        </div>
      </div>

    )
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    motos: getMotos(state, ownProps)
  }
}

const mapDispatchToProps = {
  fetchMotos,
  loadMoreMotos,
  addMotoToBasket,
  fetchCategories
}

export default connect(mapStateToProps, mapDispatchToProps)(Motos)
