import R from 'ramda'

export const getMotoById = (state, id) => R.prop(id, state.motos)

export const getActiveCategoryId = ownProps => R.path(['params', 'id'], ownProps)


export const getMotos = (state, ownProps) => {
  const activeCategoryId = getActiveCategoryId(ownProps)
  const applySearch = item => R.contains(
    state.motosPage.search,
    R.prop('name', item)
  )
  const applyCategory = item => R.equals(
    activeCategoryId,
    R.prop('categoryId', item)
  )

  const motos = R.compose(
    R.filter(applySearch),
    R.when(R.always(activeCategoryId), R.filter(applyCategory)),
    R.map(id => getMotoById(state, id))
  )(state.motosPage.ids)

  return motos
}

export const getRenderedMotosLength = state => R.length(state.motosPage.ids)

export const getTotalBasketCount = state => R.length(state.basket)

export const getTotalBasketPrice = state => {
  const totalPrice = R.compose(
    R.sum,
    R.pluck('price'),
    R.map(id => getMotoById(state, id))
  )(state.basket)

  return totalPrice
}

export const getCategories = state => R.values(state.categories)


export const getBasketMotosWithCount = state => {
  const motoCount = id => R.compose(
    R.length,
    R.filter(basketId => R.equals(id, basketId))
  )(state.basket)
  const motoWithCount = moto => R.assoc('count', motoCount(moto.id), moto)

  const uniqueIds = R.uniq(state.basket)
  const motos = R.compose(
    R.map(motoWithCount),
    R.map(id => getMotoById(state, id))
  )(uniqueIds)

  return motos
}
